mapper.select:
SELECT num, name, cnt, remark , yn ,total FROM (select rownum num, cd, cnt, yn, total from (select count(PRODUCT_COMPANY_CD) total from PRODUCT) a,
(select count(PRODUCT_COMPANY_CD) cnt, PRODUCT_COMPANY_CD cd, max(PRODUCT_DEL_YN) yn
from (select * from PRODUCT where PRODUCT_DEL_YN = 'N')
group by PRODUCT_COMPANY_CD) b) abc, (select (PRODUCT_COMPANY_NAME) name ,(PRODUCT_REMARK) remark, (PRODUCT_COMPANY_CD)cd from PRODUCT_COMPANY) bcd where abc.cd = bcd.cd ;
